import{_ as o,c as t,o as i,aj as e}from"./chunks/framework.D26WZD1w.js";const p=JSON.parse('{"title":"🚌 Road Transport","description":"","frontmatter":{"comment":false},"headers":[],"relativePath":"en-us/transportation/road.md","filePath":"en-us/transportation/road.md","lastUpdated":1758612136000}'),n={name:"en-us/transportation/road.md"};function r(s,a,l,c,u,h){return i(),t("div",null,a[0]||(a[0]=[e('<h1 id="🚌-road-transport" tabindex="-1">🚌 Road Transport <a class="header-anchor" href="#🚌-road-transport" aria-label="Permalink to &quot;🚌 Road Transport&quot;">​</a></h1><h2 id="buses-not-ride-hailing-ones" tabindex="-1">Buses (not ride-hailing ones) <a class="header-anchor" href="#buses-not-ride-hailing-ones" aria-label="Permalink to &quot;Buses (not ride-hailing ones)&quot;">​</a></h2><h3 id="common-routes" tabindex="-1">Common Routes <a class="header-anchor" href="#common-routes" aria-label="Permalink to &quot;Common Routes&quot;">​</a></h3><h4 id="connecting-bnbu-with-the-outside" tabindex="-1">Connecting BNBU with the outside <a class="header-anchor" href="#connecting-bnbu-with-the-outside" aria-label="Permalink to &quot;Connecting BNBU with the outside&quot;">​</a></h4><ul><li><strong>Route Z82</strong></li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Start &amp; End: BNBU – Tangjiawan Intercity Railway Station<br> Can reach: BNBU Phase 1 &amp; 2 Campuses; Tangjiawan Station (connections to B9, B10, K3, 70, Guangzhou–Zhuhai Intercity Railway, etc.)</p></div><ul><li><strong>Route 67</strong></li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Start &amp; End: Jinding – Baiyelin – Jinding (one-way loop)<br> Can reach: BNBU Phase 1 &amp; 2 Campuses; Gaoxin District People’s Hospital (connections to Route 3, B9, B10, Zhongshan 998); Jinding Market<br> Note: This is a one-way loop. If you’re going towards Baiyelin, the bus will pause there for a bit before heading back to Jinding. You don’t need to pay again.</p></div><h4 id="to-popular-spots-in-zhuhai-city" tabindex="-1">To popular spots in Zhuhai city <a class="header-anchor" href="#to-popular-spots-in-zhuhai-city" aria-label="Permalink to &quot;To popular spots in Zhuhai city&quot;">​</a></h4><ul><li><strong>Route 3</strong></li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Start &amp; End: Fojing (Jinding Industrial Park) – Hong Kong–Zhuhai–Macau Bridge Port Hub<br> Can reach: Gaoxin District People’s Hospital; Jinding Market; Gaoxin Government Service Centre (Yucun Stop); CUHK Fifth Affiliated Hospital; Yangming Plaza (Department Store Stop); Jiuzhou City; Jiuzhou Port (Jiuzhou Garden Stop); HZMB Zhuhai Port</p></div><ul><li><strong>Route K3</strong></li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Start &amp; End: Tangjiawan Intercity Station – Gongbei Port Terminal<br> Can reach: Innovation Development Building (transfer to Z82); Gaoxin Government Service Centre (Harbour One Stop); CUHK Fifth Affiliated Hospital; Yangming Plaza (Department Store Stop); Jiuzhou City; Gongbei Port</p></div><ul><li><strong>Route 70</strong></li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Start &amp; End: Jinding Industrial Park – Nanxi Terminal<br> Can reach: Gaoxin District People’s Hospital; Jinding Market; Tangjiawan Station (BIT Stop); Aoyuan Plaza (Santai Interchange South Stop)</p></div><ul><li><strong>Route B9</strong></li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Start &amp; End: Qianshan Terminal – Jinding Terminal<br> Can reach: Qianshan Huanyu City; Zhuhai Public Security Bureau; Tangjiawan Station (BIT Stop); Gaoxin District People’s Hospital; Jinding Market</p></div><ul><li><strong>Route B10</strong></li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Start &amp; End: Zhuhai Railway Station – Xiage Checkpoint<br> Can reach: Gongbei; Tunnel South; Tangjiawan Station (BIT Stop); Gaoxin District People’s Hospital; Jinding Market; Xiage Checkpoint (connection to Zhongshan Route 218)</p></div><h3 id="payment-methods" tabindex="-1">Payment Methods <a class="header-anchor" href="#payment-methods" aria-label="Permalink to &quot;Payment Methods&quot;">​</a></h3><ul><li><strong>Transport Cards</strong></li></ul><ol><li>Zhuhai Tong NFC or physical card (with transfer discounts)</li><li>Any national NFC or physical card with the “T-Union” logo</li></ol><ul><li><strong>Bank Cards</strong></li></ul><p>UnionPay NFC or physical cards supporting password-free payment</p><ul><li><strong>QR Codes</strong></li></ul><p>Zhuhai Tong Transit QR Code, UnionPay Cloud QuickPass Transit QR Code</p><ul><li><strong>Cash</strong></li></ul><p>Exact fare only (no change given, driverless ticketing)</p><h2 id="zhuhai-airport-express-bus" tabindex="-1">Zhuhai Airport Express Bus <a class="header-anchor" href="#zhuhai-airport-express-bus" aria-label="Permalink to &quot;Zhuhai Airport Express Bus&quot;">​</a></h2><div class="info custom-block"><p class="custom-block-title">Common route | Payment method</p><p>Beijing Institute of Technology Zhuhai – Zhuhai Airport<br> Tickets must be purchased via the “Zhuhai Airport Express” WeChat service account</p></div><h2 id="ride-hailing-buses-domestic" tabindex="-1">Ride-hailing Buses (Domestic) <a class="header-anchor" href="#ride-hailing-buses-domestic" aria-label="Permalink to &quot;Ride-hailing Buses (Domestic)&quot;">​</a></h2><div class="info custom-block"><p class="custom-block-title">Payment method</p><p>Check each operator’s app, mini-program, WeChat official account, service account, etc.</p></div><h3 id="common-operators" tabindex="-1">Common Operators <a class="header-anchor" href="#common-operators" aria-label="Permalink to &quot;Common Operators&quot;">​</a></h3><h4 id="state-owned-background" tabindex="-1">State-owned Background <a class="header-anchor" href="#state-owned-background" aria-label="Permalink to &quot;State-owned Background&quot;">​</a></h4><ul><li>Ruoyue Bus (Guangzhou Second Public Bus Company)</li><li>Xinhe Bus (Zhuhai Public Transport Group)</li></ul><h4 id="other-companies" tabindex="-1">Other Companies <a class="header-anchor" href="#other-companies" aria-label="Permalink to &quot;Other Companies&quot;">​</a></h4><ul><li>Dadui Bus</li><li>Siweimei</li><li>Xinguoxian</li><li>Yangyang Bus</li><li>Aida Bus</li></ul><h4 id="not-directly-operated" tabindex="-1">Not directly operated <a class="header-anchor" href="#not-directly-operated" aria-label="Permalink to &quot;Not directly operated&quot;">​</a></h4><ul><li>Didi Station Bus (tickets sold via Didi platform, but operated by other companies)</li></ul><h2 id="cross-border-coaches" tabindex="-1">Cross-border Coaches <a class="header-anchor" href="#cross-border-coaches" aria-label="Permalink to &quot;Cross-border Coaches&quot;">​</a></h2><div class="info custom-block"><p class="custom-block-title">Popular destinations | Payment method</p><p>Hong Kong Airport, Central Hong Kong<br> Check each operator’s app, mini-program, WeChat official account, service account, or official website</p></div>',41)]))}const m=o(n,[["render",r]]);export{p as __pageData,m as default};
