import{_ as l,c as o,o as t,ag as i}from"./chunks/framework.B4iVkFxj.js";const p=JSON.parse('{"title":"🚌 道路运输","description":"","frontmatter":{},"headers":[],"relativePath":"zh-cn/transportation/road.md","filePath":"zh-cn/transportation/road.md","lastUpdated":1756283636000}'),s={name:"zh-cn/transportation/road.md"};function r(e,a,c,n,u,d){return t(),o("div",null,a[0]||(a[0]=[i('<h1 id="🚌-道路运输" tabindex="-1">🚌 道路运输 <a class="header-anchor" href="#🚌-道路运输" aria-label="Permalink to &quot;🚌 道路运输&quot;">​</a></h1><blockquote><p>笔者：ShiJimmy</p></blockquote><h2 id="公交车-非网约" tabindex="-1">公交车（非网约） <a class="header-anchor" href="#公交车-非网约" aria-label="Permalink to &quot;公交车（非网约）&quot;">​</a></h2><h3 id="常用线路" tabindex="-1">常用线路 <a class="header-anchor" href="#常用线路" aria-label="Permalink to &quot;常用线路&quot;">​</a></h3><h4 id="连接-bnbu-与外界" tabindex="-1">连接 BNBU 与外界 <a class="header-anchor" href="#连接-bnbu-与外界" aria-label="Permalink to &quot;连接 BNBU 与外界&quot;">​</a></h4><ul><li><strong>Z82 线</strong></li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p>起讫点：BNBU - 城轨唐家湾站<br> 可到达：BNBU 一期、二期校园；唐家湾站（可换乘 B9 线、B10 线、K3 线、70 线、广珠城际等）</p></div><ul><li><strong>67 线</strong></li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p>起讫点：金鼎 - 柏叶林 - 金鼎（单环线）<br> 可到达：BNBU 一期、二期校园；高新区人民医院（可换乘 3 线、B9 线、B10 线、中山 998 线）；金鼎市场<br> 注：本线路为单环线，往柏叶林方向的车辆到达柏叶林后会稍作停留，随后开往金鼎方向，您无需再次支付车费）</p></div><h4 id="连接珠海市区热门地点" tabindex="-1">连接珠海市区热门地点 <a class="header-anchor" href="#连接珠海市区热门地点" aria-label="Permalink to &quot;连接珠海市区热门地点&quot;">​</a></h4><ul><li><strong>3 线</strong></li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p>起讫点：佛径（金鼎工业园）-大桥口岸枢纽<br> 可到达：高新区人民医院；金鼎市场；高新区政务服务中心（渔村站）；中大五院；扬名广场（百货公司站）；九洲城；九洲港码头（九洲花园站）；港珠澳大桥珠海口岸</p></div><ul><li><strong>K3 线</strong></li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p>起讫点：城轨唐家湾站-拱北口岸总站<br> 可到达：创新发展大厦（换乘 Z82 线）；高新区政务服务中心（港湾一号站）；中大五院；扬名广场（百货公司站）；九洲城；拱北口岸</p></div><ul><li><strong>70 线</strong></li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p>起讫点：金鼎工业园-南溪总站<br> 可到达：高新区人民医院；金鼎市场；唐家湾站（北理工站）；奥园广场（三台石立交南站）</p></div><ul><li><strong>B9线</strong></li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p>起讫点：前山总站 - 金鼎总站<br> 可到达：前山寰宇城；珠海市公安局；唐家湾站（北理工站）；高新区人民医院；金鼎市场</p></div><ul><li><strong>B10 线</strong></li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p>起讫点：城轨珠海站 - 下栅检查站<br> 可到达：拱北；隧道南；唐家湾站（北理工站）；高新区人民医院；金鼎市场；下栅检查站（可换乘中山 218 线）</p></div><h3 id="支付方式" tabindex="-1">支付方式 <a class="header-anchor" href="#支付方式" aria-label="Permalink to &quot;支付方式&quot;">​</a></h3><ul><li><strong>交通卡</strong></li></ul><ol><li>珠海通 NFC、实体卡（享受换乘优惠）</li><li>全国任意城市带交通联合标志的 NFC、实体卡</li></ol><ul><li><strong>银行卡</strong></li></ul><p>支持免密支付的银联 NFC、实体卡</p><ul><li><strong>二维码</strong></li></ul><p>珠海通乘车码、银联云闪付乘车码</p><ul><li><strong>现金</strong></li></ul><p>无人售票，不设找赎</p><h2 id="珠海机场巴士" tabindex="-1">珠海机场巴士 <a class="header-anchor" href="#珠海机场巴士" aria-label="Permalink to &quot;珠海机场巴士&quot;">​</a></h2><div class="info custom-block"><p class="custom-block-title">常用线路 | 支付方式</p><p>北理工-珠海机场<br> 于 “珠海机场快线” 微信服务号购票</p></div><h2 id="网约巴士-境内" tabindex="-1">网约巴士（境内） <a class="header-anchor" href="#网约巴士-境内" aria-label="Permalink to &quot;网约巴士（境内）&quot;">​</a></h2><div class="info custom-block"><p class="custom-block-title">支付方式</p><p>见各运营商 app、小程序、微信公众号、服务号等</p></div><h3 id="常见运营商" tabindex="-1">常见运营商 <a class="header-anchor" href="#常见运营商" aria-label="Permalink to &quot;常见运营商&quot;">​</a></h3><h4 id="国企背景" tabindex="-1">国企背景 <a class="header-anchor" href="#国企背景" aria-label="Permalink to &quot;国企背景&quot;">​</a></h4><ul><li>如约巴士（广州市第二公共汽车公司）</li><li>信禾巴士（珠海公交集团）</li></ul><h4 id="其它企业" tabindex="-1">其它企业 <a class="header-anchor" href="#其它企业" aria-label="Permalink to &quot;其它企业&quot;">​</a></h4><ul><li>搭对巴士</li><li>斯为美</li><li>新国线</li><li>样样巴士</li><li>爱搭巴士</li></ul><h4 id="非直接运营" tabindex="-1">非直接运营 <a class="header-anchor" href="#非直接运营" aria-label="Permalink to &quot;非直接运营&quot;">​</a></h4><ul><li>滴滴站点巴士（滴滴平台只负责售票，实际承运企业并非滴滴）</li></ul><h2 id="跨境直通巴" tabindex="-1">跨境直通巴 <a class="header-anchor" href="#跨境直通巴" aria-label="Permalink to &quot;跨境直通巴&quot;">​</a></h2><div class="info custom-block"><p class="custom-block-title">热门目的地 | 支付方式</p><p>香港机场、香港中环<br> 见各运营商 app、小程序、微信公众号、服务号、官网等</p></div>',42)]))}const b=l(s,[["render",r]]);export{p as __pageData,b as default};
